using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace AmazingArray
{
    class Program
    {
        static void Main(string[] args)
        {
            string S = "...";
            int answer = solveProblem(S);
            Console.WriteLine(answer);
            Console.ReadLine();
        }

        public static int solve(string A)
        {

            long vCount = 0;
            long totalAmazeCount = 0;

            for (int i = 0; i < A.Length; i++)
            {
                if (A[i] == 'a' || A[i] == 'e' || A[i] == 'i' || A[i] == 'o' || A[i] == 'u' ||
                 A[i] == 'A' || A[i] == 'E' || A[i] == 'I' || A[i] == 'O' || A[i] == 'U')
                {
                    vCount++;
                    totalAmazeCount = (totalAmazeCount + vCount)%10003;
                }
                else
                {
                    char a = A[i];
                    if (char.IsLetter(a))
                    {
                        totalAmazeCount = (totalAmazeCount + vCount) % 10003;
                    }
                    else
                    {
                        vCount = 0;
                    }
                }
            }

            int result = (int)totalAmazeCount % 10003;

            return result;
        }

        public static int solveProblem(string A)
        {



            int N = A.Length;

            int count = 0;

            int result = 0;

            string s = "aeiouAEIOU";

            for (int i = 0; i < N; i++)

            {

                // Every time a vowel is found the count will be increased by 1

                if (s.Contains(A[i]))
                {

                    count++;

                }

                //Adding count to the result.

                // Eg: for first occurrence count will be 1 hence the result will be increased by 1

                // Eg: for second occurrence count will be 2 hence the result will be increased by 2

                // Eg: for third occurrence count will be 3 hence the result will be increased by 3 and so on

                result = result + count;

                result = result % 10003;



            }

            return result;

        }
    }
}
